import 'package:biteblitz/src/res/imges.dart';import 'package:flutter/material.dart';import 'package:go_router/go_router.dart';import '../../../res/colors.dart';import '../../../utils/router.dart';class item_added_successfully extends StatelessWidget {  const item_added_successfully({super.key});  @override  Widget build(BuildContext context) {    final screenHeight = MediaQuery.of(context).size.height;    final screenWidth = MediaQuery.of(context).size.width;    return SafeArea(      child: Scaffold(          body: Padding(        padding: EdgeInsets.all(screenHeight * 0.03),        child: Column(            mainAxisAlignment: MainAxisAlignment.center,            crossAxisAlignment: CrossAxisAlignment.center,            children: [              Padding(                padding: EdgeInsets.symmetric(horizontal: screenHeight * 0.02),                child: Image.asset(                  AppImages.item_added,                  height: screenHeight * 0.32,                  width: screenHeight * 0.32,                ),              ),              SizedBox(height: screenHeight * 0.02),              const Text(                textAlign: TextAlign.center,                'Congratulations! Your item has been successfully added to the menu. You can now view your updated menu or add another item if needed.',                style: TextStyle(fontSize: 14, color: AppColors.dark_text_color, fontWeight: FontWeight.w600),              ),              SizedBox(height: screenHeight * 0.02),              InkWell(                onTap: () {                  context.go(routeNames.dashboard);                },                child: const Text(                  textAlign: TextAlign.center,                  'View Menu',                  style: TextStyle(fontSize: 14, color: AppColors.primary_orange_color, fontWeight: FontWeight.w600),                ),              ),            ]),      )),    );  }}